package com.project.foodapp;

import android.content.ContentValues;
import android.content.Context;
import android.database.sqlite.SQLiteDatabase;
import android.database.sqlite.SQLiteException;
import android.database.sqlite.SQLiteOpenHelper;
import android.util.Log;

import java.io.FileOutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.io.OutputStream;

public class DatabaseHelper extends SQLiteOpenHelper {

    // Имя базы данных и версия
    private static final String DATABASE_NAME = "mydatabase";
    private static final int DATABASE_VERSION = 16;

    // Конструктор
    public DatabaseHelper(Context context) {
        super(context, DATABASE_NAME, null, DATABASE_VERSION);
    }

    // Создание таблицы
    @Override
    public void onCreate(SQLiteDatabase db) {
        db.execSQL("CREATE TABLE IF NOT EXISTS Recipes (name TEXT PRIMARY KEY, section TEXT, ingredients TEXT, process TEXT, photo TEXT)");

        db.execSQL("CREATE TABLE IF NOT EXISTS Favorite (name TEXT PRIMARY KEY, ingredients TEXT, process TEXT, photo TEXT, notes TEXT)");

        db.execSQL("CREATE TABLE IF NOT EXISTS CookBook (name TEXT PRIMARY KEY, ingredients TEXT, process TEXT, photo TEXT)");
        // Добавление значения в таблицу Recipes
        ContentValues values = new ContentValues();
        values.put("name", "Vegan roast spiced squash salad with tahini dressing");
        values.put("section", "Dinner");
        values.put("ingredients","320g diced butternut squash\n" +
                "3 red onions (320g), cut into wedges\n" +
                "2 tbsp rapeseed oil\n" +
                "2 tsp smoked paprika\n" +
                "1 tsp cumin seeds\n" +
                "2 tbsp chopped thyme\n" +
                "125g quinoa\n" +
                "½ x 85g bag kale\n" +
                "2 tbsp pumpkin seeds\n" +
                "2 tbsp tahini\n" +
                "2 tbsp apple cider vinegar\n" +
                "1 garlic clove, finely grated\n" +
                "2 x 400g cans lentils or borlotti beans, very well drained\n" +
                "50g pomegranate seeds\n" +
                "4 generous handfuls of rocket");
        values.put("process", "STEP 1 Heat the oven to 200C/180C fan/gas 6. Tip the squash and onions onto a large baking sheet and toss with 1 tsp of the oil. Spread out and sprinkle with the paprika, cumin and thyme, then roast for 30 mins.\n" +
                "STEP 2 Meanwhile, cook the quinoa following pack instructions, then drain well (or the base of the salad will be too wet).\n" +
                "STEP 3 Add the kale to the tray of veg, sprinkle over the seeds and return to the oven for 10 mins.\n" +
                "STEP 4 For the dressing, mix the tahini and remaining oil with the vinegar, garlic and 2 tbsp water.\n" +
                "STEP 5\n" +
                "Put the quinoa in a bowl and toss with the lentils or beans. Pile half into a salad bowl and the rest into two lunchboxes or bowls, if you're following the Healthy Diet Plan. Divide the veg on top, then drizzle with the dressing, scatter over the pomegranate seeds and top with the rocket. Chill the other two portions for the next day. Will keep chilled for up to three days");
        values.put("photo", "data:image/jpeg;base64,");
        db.insert("Recipes", null, values);
    }

    // Обновление таблицы
    @Override
    public void onUpgrade(SQLiteDatabase db, int oldVersion, int newVersion) {
//        db.execSQL("DROP TABLE IF EXISTS Recipes");
//        db.execSQL("DROP TABLE IF EXISTS Favorite");
//        db.execSQL("DROP TABLE IF EXISTS CookBook");
        onCreate(db);
    }
}
